    host: 'db-n9jkl-kr.vpc-pub-cdb.ntruss.com',
    user: 'nodeerp',
    password: 'dnehd1008@',
    database: 'nodeERP'
});

connection.connect(err => {
    if (err) {
        console.error('Error connecting to the database:', err.stack);
        return;
    }
    console.log('Connected to the database.');
});

export default connection;

